import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProduct } from '../features/productslice';

import {
    setImage,
    setCategory,
    setPrice,
    setRating
} from '../features/productslice'


function Products() {
    const [products, setProduct] = useState([]);
    const dispatch = useDispatch();

    const product = useSelector((state) => state.products.pro);
    const image = useSelector((state) => state.products.image);
    const category = useSelector((state) => state.products.category);
    const price = useSelector((state) => state.products.price);
    const rating = useSelector((state) => state.products.rating);

    

    useEffect(() => {
        dispatch(fetchProduct())
    },[dispatch])


    return (
        <div className='container-fluid py-5'>
            <div className='row row-cols-4 m-auto '>
                {products.map((product, index) => (
                    <div className='my-3 text-center' key={index}>
                        <Card className='cart'>
                            <img
                                 src={product} className='imglogo m-auto' />
                            <Card.Body>
                                <div>
                                    <p className='fw-bold'>Rating: {product.rating.rate}</p>
                                    <p className='fw-bold'> Category: {product.category}</p>
                                    <p className='fw-bold'> Price: {product.price}</p>
                                </div>
                                <div>
                                    <Button>
                                        Add to Cart
                                    </Button>
                                </div>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    )
}

    export default Products;




































