const ADD_TO_CART = 'ADD_TO_CART';

export const addToCart = (product) => ({
    type: ADD_TO_CART,
    payload : product
});

const initialState = {
    items : [],
}

const cartReducers = (state=initialState,action) => {
    switch(action.type) {
        case ADD_TO_CART: 
            return {
                ...state ,
                items : [state.items,action.payload],
            };
            default : 
            return state;
        }
};

export default cartReducers;
