import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useDispatch } from 'react-redux';
import { fetchProduct } from '../features/productslice';

function Products() {
    const [product, setProduct] = useState([]);

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchProduct())
    },[dispatch])



    // const fetchApi = async () => {
    //     try {
    //         const res = await axios.get('https://fakestoreapi.com/products');
    //         // console.log(res);
    //         setProduct(res.data);
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };

    // useEffect(() => {
    //     fetchApi();
    // }, []);

    return (
        <div className='container-fluid py-5'>
            <div className='row row-cols-4 m-auto '>
                {product.map((products, index) => (
                    <div className='my-3 text-center' key={index}>
                        <Card className='cart'>
                            <img
                                 src={products.image} className='imglogo m-auto' />
                            <Card.Body>
                                <div>
                                    <p className='fw-bold'>Rating: {products.rating.rate}</p>
                                    <p className='fw-bold'> Category: {products.category}</p>
                                    <p className='fw-bold'> Price: {products.price}</p>
                                </div>
                                <div>
                                    <Button>
                                        Add to Cart
                                    </Button>
                                </div>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    )
}

    export default Products;




















