import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProduct } from '../features/productslice';

import {
    setImage,
    setCategory,
    setPrice,
    setRating
} from '../features/productslice'


function Products() {
    // const [products, setProduct] = useState([]);
    const dispatch = useDispatch();

    const products = useSelector((state) => state.product.products);
    const image = useSelector((state) => state.product.image);
    const category = useSelector((state) => state.product.category);
    const price = useSelector((state) => state.product.price);
    const rating = useSelector((state) => state.product.rating);

    

    useEffect(() => {
        dispatch(fetchProduct())
    },[dispatch])


    return (
        <div className='container-fluid py-5'>
        <div className='row row-cols-4 m-auto '>
          
                <div className='my-3 text-center' key={index}>
                    <Card className='cart'>
                        <img
                             src={image} className='imglogo m-auto' />
                        <Card.Body>
                            <div>
                                <p className='fw-bold'>Rating: {rating.rate}</p>
                                <p className='fw-bold'> Category: {category}</p>
                                <p className='fw-bold'> Price: {price}</p>
                            </div>
                            <div>
                                <Button>
                                    Add to Cart
                                </Button>
                            </div>
                        </Card.Body>
                    </Card>
                </div>
           
        </div>
    </div>

    )
}

    export default Products;


